            <span class="hljs-keyword" style="box-sizing: border-box;">if</span> child_cords[<span class="hljs-number" style="box-sizing: border-box;">1</span>] == prev_centroid[<span class="hljs-number" style="box-sizing: border-box;">1</span>]:
                <span class="hljs-comment" style="box-sizing: border-box;"># this means that figured moved by x coord and collided only x</span>
                <span class="hljs-keyword" style="box-sizing: border-box;">return</span> <span class="hljs-literal" style="box-sizing: border-box;">False</span>, <span class="hljs-literal" style="box-sizing: border-box;">True</span>
            <span class="hljs-keyword" style="box-sizing: border-box;">else</span>:
                <span class="hljs-keyword" style="box-sizing: border-box;">return</span> <span class="hljs-literal" style="box-sizing: border-box;">False</span>, <span class="hljs-literal" style="box-sizing: border-box;">False</span>

        <span class="hljs-comment" style="box-sizing: border-box;"># figure collision with field</span>
        <span class="hljs-keyword" style="box-sizing: border-box;">if</span> <span class="hljs-keyword" style="box-sizing: border-box;">not</span> self.min_y &lt; child_cords[<span class="hljs-number" style="box-sizing: border-box;">1</span>] &lt; self.max_y:
            <span class="hljs-keyword" style="box-sizing: border-box;">return</span> <span class="hljs-literal" style="box-sizing: border-box;">False</span>, <span class="hljs-literal" style="box-sizing: border-box;">False</span>

        <span class="hljs-keyword" style="box-sizing: border-box;">if</span> <span class="hljs-keyword" style="box-sizing: border-box;">not</span> self.min_x &lt; child_cords[<span class="hljs-number" style="box-sizing: border-box;">0</span>] &lt; self.max_x:
